package main.java.com.code.member;public class HitPoint {    private static final int MIN = 0;    int amount;    public HitPoint(int amount) {        if (amount < 0) throw new IllegalArgumentException();        this.amount = amount;    }    public void damage(int damageAmount) {        final int currentAmount = amount - damageAmount;        amount = Math.max(MIN, currentAmount);    }    public boolean isZero() {        return amount == MIN;    }}