package main.java.com.code.weapon;public class AttackPower {    private static final int MIN = 0;    private final int power;    public AttackPower(int power) {        this.power = power;    }    AttackPower reinforce(AttackPower attackPower) {        return new AttackPower(this.power + attackPower.power);    }    AttackPower disable() {        return new AttackPower(this.MIN);    }    @Override    public String toString() {        return "AttackPower{" +                "power=" + power +                '}';    }}