package main.java.discount;import main.java.product.Product;public class SummerDiscountManager {    DiscountManager discountManager;    boolean add(Product product) {        if (product.getId() < 0) throw new IllegalArgumentException();        if (product.getName().isEmpty()) throw new IllegalArgumentException();        int temp;        if (product.canDiscount()) {            temp = discountManager.totalPrice + DiscountManager.getDiscountPrice(product.getPrice());        } else {            temp = discountManager.totalPrice + product.getPrice();        }        if (temp < 300_000) {            discountManager.totalPrice = temp;            discountManager.discountProducts.add(product);            return true;        } else {            return false;        }    }}