package main.java.exam.dip;class Battery {    private final static int MIN_PERCENT = 0;    private final static int MAX_PERCENT = 100;    private final int percent;    Battery(int percent) {        if (percent == MIN_PERCENT) throw new IllegalArgumentException("충전된 배터리를 사용하세요");        this.percent = percent;    }    public int getBatteryPercent() {        return percent;    }    public boolean checkIsFull() {        return percent == MAX_PERCENT;    }}