package main.java.exam.dip;public class ElectricCigarette extends Cigarette {    private final String name;    private final int tar;    private final int nicotine;    private final Battery battery;    public ElectricCigarette(String name, int tar, int nicotine, Battery battery) {        this.name = name;        this.tar = tar;        this.nicotine = nicotine;        this.battery = battery;    }    @Override    void getCigaretteStatus() {        System.out.println("name = " + name);        System.out.println("tar = " + tar);        System.out.println("nicotine = " + nicotine);        System.out.println("battery % = " + battery.getBatteryPercent());    }    @Override    String getName() {        return name;    }}