package main.java.exam.dip;class Tobacco extends Cigarette {    private final String name;    private final int tar;    private final int nicotine;    Tobacco(String name, int tar, int nicotine) {        this.name = name;        this.tar = tar;        this.nicotine = nicotine;    }    @Override    void getCigaretteStatus() {        System.out.println("name = " + name);        System.out.println("tar = " + tar);        System.out.println("nicotine = " + nicotine);    }    @Override    String getName() {        return name;    }}