package main.java.exam.isp;// 인터페이스를 분리하여 ICar, IBoat를 구현하였다.class AmphibiousATV implements ICar, IBoat {    @Override    public void drive() {        System.out.println("수륙양용차를 땅에서 운전함");    }    @Override    public void turnLeft() {        System.out.println("수륙양용차가 땅에서 좌회전함");    }    @Override    public void turnRight() {        System.out.println("수륙양용차가 땅에서 우회전함");    }    @Override    public void steer() {        System.out.println("수륙양용차를 물에서 운전함;");    }    @Override    public void steerLeft() {        System.out.println("수륙양용차가 물에서 좌회전함");    }    @Override    public void steerRight() {        System.out.println("수륙양용차가 물에서 우회전함");    }}