package main.java.member;public class Equipments {    private boolean canChange;    private Equipment head;    private Equipment armor;    private Equipment arm;    void equipArmor(final Equipment newArmor) {        if (canChange) {            this.armor = newArmor;        }    }    void deActiveAll() {        head = Equipment.EMPTY;        armor = Equipment.EMPTY;        arm = Equipment.EMPTY;    }}