package main.java.member;public class Member {    private final HitPoint hitPoint;    private final States states;    public Member(HitPoint hitPoint, States states) {        this.hitPoint = hitPoint;        this.states = states;    }    public void damage(int damageAmount) {        hitPoint.damage(damageAmount);        if (hitPoint.isZero()) {            states.add(StateType.DEAD);        }    }}