package main.java.price;public class SummerDiscountPrice {    private static final int MIN_AMOUNT = 0;    private static final int DISCOUNT_AMOUNT = 3000;    private final int amount;    SummerDiscountPrice(final RegularPrice price) {        int discountedAmount = price.getAmount() - DISCOUNT_AMOUNT;        if (discountedAmount < 0) {            discountedAmount = MIN_AMOUNT;        }        this.amount = discountedAmount;    }}